p = 9223372036801560577
N = 4096
Na = 64
Nb = 64

invMod = lambda y,p:pow(y,p-2,p)
bitreversal = lambda n,width:int('{:0{width}b}'.format(n, width=width)[::-1], 2)

def schoolbook_mul(a, b):
    assert len(a) == len(b)
    
    N = len(a)
    c = [0]*N

    for i in range(N):
        for j in range(N):
            v = a[i]*b[j]*(-1)**(int((i+j)//float(N)))
            c[(i+j) % N] = (c[(i+j) % N] + v) % p
    return c   

def gen_polynomial_modp(length):
    x = []
    for i in range(length):
        #x.append(random.randrange(0,p))
        x.append(1)
    return x

def hierarchical_ntt(poly_x):
    assert(len(poly_x) == N)

    x = poly_x

    x = sum([ntt(x[i*Nb:i*Nb+Nb],Nb,root_64) for i in range(Na)], [])

    # for i in range(Na):
    #     for j in range(Nb):
    #         x[i*Nb+j] = (x[i*Nb+j]*pow(factor,i*j,p)) % p

    x_ntt = transpose([ntt(x[i::Nb],Na,root_64) for i in range(Nb)]).reshape(Na*Nb).tolist()

    return x_ntt

def hierarchical_intt(poly_x_ntt): # The matrix is transposed!
    assert(Na*Nb == N)
    assert(len(poly_x_ntt) == N)

    x_ntt = poly_x_ntt
    
    x_ntt = sum([intt(x_ntt[i*Na:i*Na+Na],Na,rootinv_64) for i in range(Nb)], [])

    # for i in range(Nb):
    #     for j in range(Na):
    #         x_ntt[i*Na+j] = (x_ntt[i*Na+j]*pow(factor_inv,i*j,p)) % p

    x = transpose(([intt(x_ntt[i::Na],Nb,rootinv_64) for i in range(Na)])).reshape(Na*Nb).tolist()

    return x

def poly_mul_pointwise(x_ntt, y_ntt):
    xy = [(x*y) % p for x,y in zip(x_ntt, y_ntt)]
    return xy

def ntt(x,N,root):
    a = list(x)
    log_N = int(log(N,2))
    t = N
    m = 1
    while m < N:
        t >>= 1
        for i in range(m):
            j1 = 2*i*t
            j2 = j1+t-1
            S = pow(root,bitreversal(m+i,log_N),p)
            for j in range(j1,j2+1):
                U = a[j]
                V = a[j+t]*S
                a[j] = (U+V)%p
                a[j+t] = (U-V)%p
        m<<=1
    return a

def intt(x,N,rootinv):    
    a = list(x)
    log_N = int(log(N,2))
    t = 1
    m = N
    while m > 1:
        j1 = 0
        h = m >> 1
        for i in range(h):
            j2 = j1 + t - 1
            S = pow(rootinv,bitreversal(h+i,log_N),p)
            for j in range(j1, j2+1):
                U = a[j]
                V = a[j+t]
                a[j] = (U+V)%p
                a[j+t] = ((U-V)*S)%p
            j1 = j1 + 2*t
        t <<= 1
        m >>= 1

    for j in range(N):
        a[j] = (a[j]*invMod(N,p))%p
    return a

def hierarchical_ntt_mul(a, b):
    
    a_ntt = hierarchical_ntt(a)
    b_ntt = hierarchical_ntt(b)

    c_ntt = poly_mul_pointwise(a_ntt, b_ntt)

    c = hierarchical_intt(c_ntt)

    return c 

